import React, { Component } from 'react'
import axios from "axios"

export class AddNote extends Component {
    constructor(props) {
        super(props)
    
        this.state = {
             title: "",
             body: ""
        }
    }
    changeHandler = (e) => {
        this.setState({[e.target.name]: e.target.value})
    }
      
    submitHandler = e => {
        e.preventDefault()
        console.log(this.state)
    }

    returnHome(){
        window.location.href="http://localhost:3000/"
    }
    
    render() {
        const { title, body } = this.state
        return (
            <div className='container text-center'>
                <form onSubmit={this.submitHandler}>
                    <div className='form-group'>
                        <div className='row'>
                            <div className='col'>
                                <input type="text" className="form-control-lg" placeholder="Enter Title" name="title" value={title} onChange={this.changeHandler}></input>
                            </div>
                        </div>
                        <div className='row'>
                            <div className='col'>
                                <textarea type="textarea" className="textarea" placeholder="Enter Note Here" rows="10" cols="50" name="body" value={body} onChange={this.changeHandler}></textarea>
                            </div>
                        </div>
                    </div>
                    <div className='row'>
                        <div className='col text-center'>
                            <button type="submit" className='btn btn-success ml-2'>Submit</button>
                            <button onClick={this.returnHome} className='btn btn-danger ml-2'>Cancel</button>
                        </div>
                    </div>
                </form>
            </div>
        )
    }
}
